import React, { useState } from 'react';
import ReactDOM from 'react-dom';

const Button = ({ handleClick, text }) => {
  return <button onClick={handleClick}>{text}</button>;
};

const Display = ({ count, text }) => {
  return (
    <div>
      <p>
        {text} {count}
      </p>
    </div>
  );
};

const App = () => {
  const [stats, setStats] = useState({
    good: 0,
    neutral: 0,
    bad: 0,
    total: 0,
    average: 0,
    positive: 0,
  });

  const rateGood = () => {
    setStats(...stats, good + 1, total + 1, average + 1);
    setGood(good + 1);
    setTotal(total + 1);
    setAverage(average + 1);
  };
  const rateNeutral = () => {
    setNeutral(neutral + 1);
    setTotal(total + 1);
  };
  const rateBad = () => {
    setBad(bad + 1);
    setTotal(total + 1);
    setAverage(total / 3);
  };

  return (
    <div>
      <h1>Give Feedback</h1>
      <Button handleClick={rateGood} text="Good" />
      <Button handleClick={rateNeutral} text="Neutral" />
      <Button handleClick={rateBad} text="Bad" />

      <h1>Statistics </h1>
      <Display count={good} text="Good" />
      <Display count={neutral} text="Neutral" />
      <Display count={bad} text="Bad" />
      <Display count={total} text="Total stats" />
      <Display count={average} text="Average" />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById('root'));
